package example;

public class PatternAddition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j,n=4;
		int number=1;
		for(i=1;i<=4;i++)
		{
			for(j=1;j<=i;j++)
			{
				System.out.print(" "+number);
				number++;
				
			}
			System.out.println();
		}
	}

}
